yacc_rule:
| DILSF_ABL_C idx /* . ability_costs */
{
    INITEXP($$);
    copy_code(&($$), &($2));
    $$.rtyp = DilVarType_e::DILV_UP;
    $$.typ = DilVarType_e::DILV_INT;
    $$.dsl = DSL_LFT;
    $$.num = DILF_ABL_C;
    FREEEXP($2);
}

c_implementation:
case DILF_ABL_C:
            v2 = p->stack.pop(); /* evaluate index */
            switch (dil_getval(v2))
            {
                case DILV_NULL: /* not applicable */
                case DILV_FAIL:
                    v->type = DILV_FAIL; /* not applicable */
                    break;

                case DILV_INT:
                    v->type = DILV_INT;
                    break;

                default:
                    v->type = DILV_ERR; /* wrong type */
                    break;
            }

            switch (dil_getval(v1))
            {
                case DILV_FAIL:
                    if (v->type != DILV_ERR)
                    {
                        v->type = DILV_FAIL; /* not applicable */
                    }
                    break;
                case DILV_UP:
                    break;
                default:
                    v->type = DILV_ERR; /* wrong type */
                    break;
            }

            if (v->type == DILV_INT)
            {
                if (v1->val.ptr && ((unit_data *)v1->val.ptr)->isPC() && is_in(v2->val.num, 0, ABIL_TREE_MAX - 1))
                {
                    v->atyp = DILA_NONE;
                    v->type = DILV_INT;
                    v->val.num = get_racial_ability(CHAR_RACE((unit_data *)v1->val.ptr), v2->val.num);

                    /*if (p->frame[0].tmpl->zone->access == 0)
                    {
                        v->atyp = DILA_NONE;
                        v->type = DILV_UINT1R;
                        v->ref = &PC_ABI_COST((class unit_data *)
                                                  v1->val.ptr,
                                              v2->val.num);
                    }
                    else
                    {
                        v->atyp = DILA_NONE;
                        v->type = DILV_INT;
                        v->val.num = PC_ABI_COST((class unit_data *)
                                                     v1->val.ptr,
                                                 v2->val.num);
                    }*/
                }
                else
                {
                    v->type = DILV_FAIL;
                }
            }
            break;

