yacc_rule:
| DILSE_CKPWD '(' dilexp ',' dilexp ')'
{
    INITEXP($$);

    if ($3.typ != DilVarType_e::DILV_UP)
    {
        dilfatal("Arg 1 of 'check_password' not a unitptr");
    }
    if ($5.typ != DilVarType_e::DILV_SP)
    {
        dilfatal("Arg 2 of 'check_password' not a string");
    }
    else
    {
        /* Type is ok */
        /* Make nodes dynamic */
        $$.dsl = DSL_DYN;
        $$.typ = DilVarType_e::DILV_INT;
        make_code(&($3));
        make_code(&($5));
        add_code(&($$), &($3));
        add_code(&($$), &($5));
        add_ubit8(&($$), DILE_CKPWD);
    }
    FREEEXP($5);
}

c_implementation:
void dilfe_ckpwd(dilprg *p)
{
    dilval *v = new dilval;

    dilval *v2 = p->stack.pop();
    dilval *v1 = p->stack.pop();

    v->type = DILV_INT;

    switch (dil_getval(v1))
    {
        case DILV_UP:
            switch (dil_getval(v2))
            {
                case DILV_SP:
                    if (!((unit_data *)v1->val.ptr)->isPC())
                    {
                        szonelog(p->sarg->owner->getFileIndex()->getZone(),
                                 "DIL %s, Illegal: Unit must be a pc to check password.\n",
                                 p->sarg->owner->getFileIndexSymName());
                        v->type = DILV_FAIL;
                        break;
                    }

                    v->type = DILV_INT;
                    if (pwdcompare(crypt((char *)v2->val.ptr, PC_FILENAME((unit_data *)v1->val.ptr)),
                                   PC_PWD((unit_data *)v1->val.ptr),
                                   pc_data::PC_MAX_PASSWORD))
                    {
                        v->val.num = FALSE;
                    }
                    else
                    {
                        v->val.num = TRUE;
                    }

                    break;
                case DILV_FAIL:
                case DILV_NULL:
                    v->type = DILV_FAIL;
                    break;
                default:
                    v->type = DILV_ERR;
            }

            break;
        case DILV_FAIL:
        case DILV_NULL:
            v->type = DILV_FAIL;
            break;
        default:
            v->type = DILV_ERR;
    }
    p->stack.push(v);
    delete v1;
    delete v2;
}

