yacc_rule:
| DILSE_GETCLR '(' dilexp ')'
{
    INITEXP($$);

    if ($3.typ != DilVarType_e::DILV_SP)
    {
        dilfatal("Arg 1 of 'getcolor' not a string");
    }
    else
    {
        /* Type is ok */
        /* Make nodes dynamic */
        $$.dsl = DSL_DYN;
        $$.typ = DilVarType_e::DILV_SP;
        make_code(&($3));
        add_code(&($$), &($3));
        add_ubit8(&($$), DILE_GETCLR);
    }
    FREEEXP($3);
}

c_implementation:
void dilfe_clr(dilprg *p)
{
    dilval *v = new dilval;
    dilval *v1 = p->stack.pop();

    v->type = DILV_SP;

    switch (dil_getval(v1))
    {
        case DILV_SP:
            v->atyp = DILA_EXP;
            v->val.ptr = str_dup(divcolor((char *)v1->val.ptr));
            break;
        case DILV_FAIL:
        case DILV_UP:
        case DILV_NULL:
            v->type = DILV_FAIL;
            break;
    }
    p->stack.push(v);
    delete v1;
}

