yacc_rule:
| DILSE_MID '(' dilexp ',' dilexp ',' dilexp ')'
{
    INITEXP($$);

    if ($3.typ != DilVarType_e::DILV_SP)
    {
        dilfatal("Arg 1 of 'mid' not a string.");
    }
    if ($5.typ != DilVarType_e::DILV_INT)
    {
        dilfatal("Arg 2 of 'mid' not an integer.");
    }
    if ($7.typ != DilVarType_e::DILV_INT)
    {
        dilfatal("Arg 3 of 'mid' not an integer.");
    }
    else
    {
        /* Type is ok */
        /* Make nodes dynamic */
        $$.dsl = DSL_DYN;
        $$.typ = DilVarType_e::DILV_SP;
        make_code(&($3));
        make_code(&($5));
        make_code(&($7));
        add_code(&($$), &($3));
        add_code(&($$), &($5));
        add_code(&($$), &($7));
        add_ubit8(&($$), DILE_MID);
    }
    FREEEXP($3);
}

c_implementation:
void dilfe_mid(dilprg *p)
{
    dilval *v = new dilval;
    dilval *v3 = p->stack.pop();
    dilval *v2 = p->stack.pop();
    dilval *v1 = p->stack.pop();
    int strl = 0;
    int l = 0;
    int r = 0;
    int x = 0;

    v->type = DILV_SP;

    switch (dil_getval(v1))
    {
        case DILV_FAIL:
        case DILV_NULL:
            v->type = DILV_FAIL;
            break;
        default:
            v->type = DILV_ERR;
            break;
        case DILV_SP:
            switch (dil_getval(v2))
            {
                case DILV_FAIL:
                case DILV_NULL:
                    v->type = DILV_FAIL;
                    break;
                default:
                    v->type = DILV_ERR;
                    break;
                case DILV_INT:

                    switch (dil_getval(v3))
                    {
                        case DILV_FAIL:
                        case DILV_NULL:
                            v->type = DILV_FAIL;
                            break;
                        default:
                            v->type = DILV_ERR;
                            break;
                        case DILV_INT:
                            if ((v2->val.num >= 0) && ((int)strlen((char *)v1->val.ptr) < v2->val.num))
                            {
                                v1->val.ptr = nullptr;
                                break;
                            }
                            else
                            {
                                l = v2->val.num;
                            }

                            if ((v2->val.num >= 0) && ((int)strlen((char *)v1->val.ptr) < v3->val.num))
                            {
                                r = strlen((char *)v1->val.ptr);
                            }
                            else
                            {
                                r = v3->val.num;
                            }

                            if (r < l)
                            {
                                szonelog(p->sarg->owner->getFileIndex()->getZone(),
                                         "DIL %s, Illegal: mid(1,2,3) 3 > 2 \n",
                                         p->sarg->owner->getFileIndexSymName());
                                v->type = DILV_FAIL;
                                break;
                            }

                            strl = (r - l) + 1;

                            if (str_is_empty((char *)v1->val.ptr) || strl == 0)
                            {
                                v->val.ptr = nullptr;
                            }
                            else
                            {
                                v->atyp = DILA_EXP;
                                v->val.ptr = malloc(strl + 1);
                                for (x = 0; l <= r; l++, x++)
                                {
                                    ((char *)v->val.ptr)[x] = ((char *)v1->val.ptr)[l - 1];
                                }
                                ((char *)v->val.ptr)[strl] = 0;
                            }

                            break;
                    }
                    break;
            }
            break;
    }
    p->stack.push(v);
    delete v1;
    delete v2;
    delete v3;
}

