yacc_rule:
| DILSE_RHEAD '(' ')'
{
    INITEXP($$);
    $$.dsl = DSL_DYN;
    $$.typ = DilVarType_e::DILV_UP;
    add_ubit8(&($$), DILE_RHEAD);
}

c_implementation:
void dilfe_rhead(dilprg *p)
{
    dilval *v = new dilval;
    if (g_room_head->isRoom())
    {
        v->atyp = DILA_NORM;
        v->type = DILV_UP;
        v->val.ptr = g_room_head;
    }
    else
    {
        v->type = DILV_NULL;
    }
    p->stack.push(v);
}

